mapper.update:
UPDATE CLASS
SET CLASS_NAME = #{className }, CLASS_ADDR = #{classAddr }, CLASS_TYPE = #{classType }, CLASS_TYPEDETAILS = #{classTypedetails }, 
CLASS_INFO = #{classInfo }, CLASS_PRICE = #{classPrice }, CLASS_START_DATE = #{classStartDate }, CLASS_END_DATE = #{classEndDate }, CLASS_START_TIME = #{classStartTime }, CLASS_END_TIME = #{classEndTime}, C_ORIGINAL_FILENAME = #{cOriginalFilename }, C_RENAME_FILENAME = #{cRenameFilename }
WHERE CLASS_NO = #{classNo }

UPDATE CLASS
SET CLASS_STATUS = 'N'
WHERE CLASS_NO = #{classNo }
